package day0526;

public class VarTest_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//변수는 메모리 공간 할당과 접근 2가지 문제를 해결하기 위해 도입
		//변수 선언 메모리공간 할당 메모리공간에 접근하기 위해 이름
		//자료형 참조변수명=값;
		int num1=20;
		int num2;
		num2=50;
		int add=num1+num2;
		System.out.println("2숫자의 합은"+num1+","+num2+"입니다");
		System.out.println("두숫자의 합은"+add+"입니다");
	}

}
